-- Ensure id column can handle sequence and any existing string IDs
ALTER TABLE events ALTER COLUMN id TYPE TEXT;

-- Create sequence if missing
CREATE SEQUENCE IF NOT EXISTS events_id_seq;

-- Set default and sync
ALTER TABLE events ALTER COLUMN id SET DEFAULT nextval('events_id_seq');
ALTER SEQUENCE events_id_seq OWNED BY events.id;

-- Safer sequence sync: Only consider numeric IDs for synchronization
SELECT setval('events_id_seq', COALESCE((SELECT MAX(id::bigint) FROM events WHERE id ~ '^[0-9]+$'), 0) + 1, false);


